import {makeAutoObservable} from "mobx";

export default class DeviceStore{
    constructor() {
        this._types = [
            {id: 1, name: 'Холодильники'},
            {id: 2, name: 'Смартфони'},
            {id: 3, name: 'Ноутбуки'},
            {id: 4, name: 'Телевізори'},
        ]
        this._brands = [
            {id: 1, name: 'Samsung'},
            {id: 2, name: 'Apple'}

        ]
        this._devices = [
            {id:1,	name:"12 rpo", price:10000, rating:0, img:"ab7d729b-c88f-44d4-ad39-6026c4368344.jpg"},
            {id:4,	name:"12 pro",	price:10000, rating:0, img:"e8a0d166-1a74-4d34-8847-8e04c7fee810.jpg"},
            {id:6,	name:"12 pro.",	price:	10000, rating:0, img:"52206a88-5290-4e36-8289-944e670f4602.jpg"},
            {id:7,	name:"12 Pro",	price:170000, rating:0,	img:"83e4bb74-b1a5-43b2-b35a-55943ae1d474.jpg"}
        ]
        this._selectedType = {}
        this._selectedBrand = {}
        makeAutoObservable(this)
    }
    setTypes(types){
        this._types = types
    }
    setBrands(brands){
        this._brands = brands
    }
    setDevices(devices){
        this._devices = devices
    }
    setSelectedType(type){
        this._selectedType = type
    }
    setSelectedBrand(brand){
        this._selectedBrand = brand
    }
    get types() {
        return this._types
    }
    get brands() {
        return this._brands
    }
    get devices() {
        return this._devices
    }
    get selectedType() {
        return this._selectedType
    }
    get selectedBrand() {
        return this._selectedBrand
    }
}
