import React, {useContext, useState} from 'react';
import {Button, Dropdown, DropdownItem, DropdownMenu, DropdownToggle, Form, FormControl, Modal} from "react-bootstrap";
import {Context} from "../../index";

const CreateDevice = ({show, onHide}) => {
    const {device} = useContext(Context)
    const  [info, setInfo] = useState([])

    const addInfo = () => {
      setInfo([...info, {title: '', description: '', number: Date.now()}])
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавити девайс
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown className="mt-2">
                        <DropdownToggle>Виберіть тип</DropdownToggle>
                        <DropdownMenu>
                            {device.types.map(type =>
                                <DropdownItem key={type.id}>
                                    {type.name}
                                </DropdownItem>
                            )}
                        </DropdownMenu>
                    </Dropdown>
                    <Dropdown className="mt-2">
                        <DropdownToggle>Виберіть бренд</DropdownToggle>
                        <DropdownMenu>
                            {device.brands.map(brand =>
                                <DropdownItem key={brand.id}>
                                    {brand.name}
                                </DropdownItem>
                            )}
                        </DropdownMenu>
                    </Dropdown>
                    <FormControl
                        className="mt-3"
                        placeholder="Введіть назву девайса"
                    />
                    <FormControl
                        className="mt-3"
                        placeholder="Введіть вартість девайса"
                        type="number"
                    />
                    <FormControl
                        className="mt-3"
                        type="file"
                    />
                    <hr />
                    <Button
                        onClick={addInfo}
                        variant={"outline-dark"}
                    >
                        Добавити нову властивість
                    </Button>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрити</Button>
                <Button variant="outline-success" onClick={onHide}>Добавити</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateDevice;
