import React, {useContext} from 'react';
import {Context} from "../index";
import {Button, Container, Nav} from "react-bootstrap";
import {Navbar} from "react-bootstrap";
import {NavLink} from "react-router-dom";
import {SHOP_ROUTE} from "../utils/consts";
import {observer} from "mobx-react-lite";

const NavBar = observer(() => {
    const {user} = useContext(Context)
    return (

            <Navbar bg="Danger"  variant="Danger">
                <Container>
                    <NavLink className='text-decoration-none' style={{color:'white',marginRight: 'auto'}} to={SHOP_ROUTE}>
                        WorkPhone
                    </NavLink>
                        {user.isAuth ? (
                                 <Nav className="ml-auto" style={{ color: 'white' }}>
                                    <Button variant="outline-light">Адмін панель</Button>
                                    <Button variant="outline-light" style={{ marginLeft: '1rem' }}>Увійти</Button>
                                </Nav>
                             ) :
                            (   <Nav className="ml-auto" style={{ color: 'white' }}>
                                    <Button variant={"outline-light"} onClick={() => user.setIsAuth(true)}>Авторизація</Button>
                                </Nav>

                             )}
                </Container>
            </Navbar>
         );
    });

export default NavBar;
