import React from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import bigStar from '../assets/bigStar.png'

const DevicePage = () => {
    const device = {id:1,	name:"Iphone 12 rpo", price:10000, rating:5, img:"ab7d729b-c88f-44d4-ad39-6026c4368344.jpg"}
    const description = [
        {id:1, title:'Оперативна память', description:'5'},
        {id:2, title:'Камера', description:'12 мп'},
        {id:3, title:'Процессор', description:'Пентіум 3'},
        {id:4, title:'Кількість ядер', description:'5'},
        {id:5, title:'Аккумулятор', description:'5'},
    ]
    return (
     <Container className="mt-3">
         <Row>
             <Col md={4}>
                 <Image width={300} height={300} src={device.img}/>
             </Col>
             <Col md={4}>
                 <Row className="d-flex flex-column justify-content-center align-items-center">
                     <h2>{device.name}</h2>
                     <div
                         className="d-flex align-items-center justify-content-center"
                         style={{background: `url(${bigStar}) no-repeat center center `, width: 240, height:240, backgroundSize: 'cover', fontSize:64}}
                     >
                         {device.rating}
                     </div>
                 </Row>

             </Col>
             <Col md={4}>
                <Card
                    className="d-flex flex-column align-items-center justify-content-around"
                    style={{width: 300, height:300, fontSize:32, border: '5px solid lightgray'}}
                >
                    <h3>Від: {device.price} грн.</h3>
                    <Button variant={"outline-dark"}>Добавити в корзину</Button>
                </Card>
             </Col>
         </Row>

     </Container>
    );
};

export default DevicePage;
